import "./homePage.css"
import React, { useEffect, useState } from "react";

function getWindowWidth()
{
    const { innerWidth: width} = window;
    return {
      width
    };
}

export default function HomePage() {

    //An of raids to display to the home page
    const [width, setWidth] = useState(getWindowWidth());

    
    //Use effect hock sets the intial condition?
   useEffect(()=>{
       function handleResize(){
           setWidth(getWindowWidth());
       }
       window.addEventListener("resize", handleResize);
       return () => window.removeEventListener("resize", handleResize);

   },[]);
      

    return (
        <div>
            <h1>Lost Ark</h1>
            <h2>Popular Raids</h2>

            <h2>Popular Pages</h2>

            <p>You clicked {width} times</p>
            {/*Creates a function which increments count by 1 using set state*/}
            <button onClick={() => setWidth(window.innerWidth)}>
                Click me
            </button>
            
        </div>
    );
}
