import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink, useNavigate } from "react-router-dom";

export default function Profile() {
  //Copy Brecon
  const [data, setData] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPrivateData = async () => {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      };

      try {
        const { data } = await axios.get("/accountPage", config);
        setData(data.data);
      } catch (error) {
        localStorage.removeItem("authToken");
        setError("Not authorized, please login, redirecting to login page...");
        setTimeout(() => {
          navigate("/login");
        }, 3000);
      }
    };

    fetchPrivateData();
  });

  return error ? (
    error
  ) : (
    <div className="FullProfile">
      <div class="row">
        <div class="col-md-10">
          <div class="row">
            <div class="col-md-4">
              <h2>{data.username}</h2>
              {/* <p>
                <img 
                    src= {data.profilePhoto}
                    alt="new"
                    /></p> */}
            </div>
            <div class="col-md-8">
              <h2>Character Information</h2>
              <p>Class: {data.characterClass}</p>
              {/* <p>Skills: {data.skills}</p>
                <p>Builds: {data.builds}</p> */}
            </div>
          </div>
          <div class="row">
            <div class="col-md-10">
              <h2>About Me</h2>
              <p>{data.aboutMe}</p>
            </div>
          </div>
        </div>
        <div class="col-md-2">
          <NavLink to="/editProfile">Edit Profile</NavLink>
          {/* <h2 class="text-center">Friends</h2>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p>
            <p class="text-center">Friends</p> */}
        </div>
      </div>
    </div>
  );
}
